}

/**
* @func xyz2ciede
* @desc Return the CIEDE2000 difference between 2 XYZ colors
* @param {ArrayXYZ} xyz1
* @param {ArrayXYZ} xyz2
* @return {NumberCIEDE}.
* @example
* xyz2ciede([95.05, 100, 108.88], [0, 0, 0]) // => 100
*/

function xyz2ciede(xyz1, xyz2) {
	return lab2ciede(xyz2lab(...xyz1), xyz2lab(...xyz2));
}

